package org.jusun.spring_js.controller;import lombok.RequiredArgsConstructor;import org.jusun.spring_js.model.request.LoginRequest;import org.jusun.spring_js.service.LoginService;import org.jusun.spring_js.utilise.SecurityUtils;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/login")@RequiredArgsConstructorpublic class LoginController {    public final LoginService loginService;    @PostMapping    public String login(            @RequestBody LoginRequest request) {        return "Hello " + request.username + " " + SecurityUtils.encodeSHA256WithSalt(request.password) ;    }}